// Title: HomeContent.js
// Author: Joel Harawa
import React, {useContext, useEffect, useState} from "react";
import styled, {keyframes} from "styled-components";
import { AuthContext } from "../AuthContext";
import { Link } from "react-router-dom";

const HomeContent = () => {
    let moodData = require("../data/moods.json")
    const [searchInput, setSearchInput] = useState("");
    const [userMood, setUserMood] = useState("");
    const [username, setUsername] = useState("Guest");
    const { token } = useContext(AuthContext);
    console.log(token);

    useEffect(() => { 
        const fetchData = async () => {
            try {
                if (token) {
                    const response = await fetch("https://api.spotify.com/v1/me", {
                        headers: {
                            Authorization: `Bearer ${token}`,
                        },
                    });

                    if (!response.ok) {
                        throw new Error("Failed to fetch data");
                    }

                    const data = await response.json();
                    setUsername(data.display_name || "Guest");
                    console.log(data.display_name);
                } else {
                    console.log("No token found, using default Guest username.");
                    setUsername("Guest");
                }
            } catch (error) {
                console.error("Error fetching data:", error);
                setUsername("Guest");
            }
        };

        fetchData();
    }, [token]);

    const handleSearch = (event) => {
        setSearchInput(event.target.value);
    }

    const filteredMoods = moodData.moods.filter((mood) =>
        mood.toLowerCase().includes(searchInput.toLowerCase())
    );

    return (
        <Container>
            <Content>
                <SearchBox>
                    <Greeting>
                        <GreetingText>
                            Hi {username}, How are we feeling today?
                        </GreetingText>
                    </Greeting>
                    <Search>
                        <SearchBar 
                            placeholder="Enter your mood..."
                            value={searchInput}
                            onChange={handleSearch}
                        />
                    </Search>
                    {
                            searchInput.length > 0 && (
                                <Dropdown>
                                    {
                                        filteredMoods.map((mood, index) => (
                                            <Link to="/view" key={index}>
                                                <Result>{mood}</Result>
                                            </Link>
                                        ))
                                    }
                                </Dropdown>
                            )
                    }
                </SearchBox>
            </Content>
        </Container>
    );
};

const AnimationName = keyframes`
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
`;

const Container = styled.div`
    height: 90%;
    width: 100%;
    background: linear-gradient(234deg, #A239CA, #0E0B16);
    background-size: 400% 400%;
    animation: ${AnimationName} 10s ease infinite;
`;

const Content = styled.div`
    display: flex;
    justify-content: center;
    height: 100%;
    width: 100%;
    overflow: hidden;
`;

const SearchBox = styled.div`
    margin-top: 10%;
    display: flex;
    flex-direction: column;
    height: 25%;
    width: 50%;
    border-radius: 25px;
    background-color: #2B2B2B;
`;

const Greeting = styled.div`
    display: flex;
    margin: 10px;
    width: 100%;
    height: 60%;
`;

const GreetingText = styled.a`
    font-family: "Poppins", system-ui;
    font-size: 2.5em;
    color: #4717F6;
    padding-left: 25px;
`;

const Search = styled.div`
    display: flex;
    justify-content: center;
    width: 100%;
    height: 40%;
`;

const SearchBar = styled.input`
    width: 90%;
    height: 30%;
    padding: 10px;
    border: none;
    border-radius: 25px;
    font-size: 1.5em;
    background-color: #2B2B2B;
    color: #A239CA;
    outline: none;
`;

const Dropdown = styled.ul`
    width: 90%;
    list-style: none;
    padding-left: 0vh;
    background-color: #2B2B2B;
    border-radius: 25px;
    margin-left: 5%;
`;

const Result = styled.li`
    font-family: "Poppins", system-ui;
    color: #A239CA;
    font-size: 1.5em;
    padding: 10px;
    border-radius: 25px;
    margin: 0;
    a {
        text-decoration: none;
    }
    &:hover {
        a {
            text-decoration: none;
        }   
        color: white;
        cursor: pointer;
        background-color: #4717F6;
    }
`;

export default HomeContent;
