// Title: Navbar.js
// Author: Joel Harawa
// Purpose: Implement Navbar for users on the website

import styled from "styled-components";
import isMobile from "is-mobile";
import { useContext, useEffect, useState } from "react";
import { AuthContext } from "../AuthContext";

const Navbar = () => {
    const token = window.localStorage.getItem("token");
    const [username, setUsername] = useState("");
    const [photo, setPhoto] = useState("https://img.icons8.com/?size=100&id=z-JBA_KtSkxG&format=png&color=000000");
    const {login, logout} = useContext(AuthContext);
    const [isMobileDevice, setIsMobileDevice] = useState(false);

    useEffect(() => {
        setIsMobileDevice(isMobile());
    }, [isMobileDevice]);

    useEffect(() => { 
        const fetchData = async () => {
            if (token !== null) {
                console.log(token)
                await fetch("https://api.spotify.com/v1/me", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then(response => response.json())
                .then(data => {
                    setUsername(data.display_name);
                    setPhoto(data.images);
                    console.log(data.display_name);
                    console.log(data.images);
                })
            
            } else {
                console.log("token")
                setUsername("Guest");
                setPhoto("https://img.icons8.com/?size=100&id=z-JBA_KtSkxG&format=png&color=000000");
            }
        }
        fetchData();
    }, [token]);

    useEffect(() => {
        const handleStorageChange = () => {
            setToken(window.localStorage.getItem("token"));
        }

        window.addEventListener("storage", handleStorageChange);

        return(
            window.removeEventListener("storage", handleStorageChange)
        )
    }, [])



    return (
        <Container>
            <Left/>
            <Middle>
                <Logo ismobile={isMobileDevice.toString()}>Moosic</Logo>
            </Middle>
                {token ? 
                    <Right>
                        <ProfileContainer ismobile={isMobileDevice.toString()} onClick={logout}>
                            <ProfilePhoto src={photo}/>
                        </ProfileContainer>
                        <MenuItem href="/">Logout</MenuItem>
                    </Right>:
                    <Right>
                        <ProfileContainer ismobile={isMobileDevice.toString()} onClick={login}>
                            <ProfilePhoto src={photo}/>
                        </ProfileContainer>
                    </Right>
                }
        </Container>
    )
}

const Container = styled.div`
    background-color: #2B2B2B;
    display: flex;
    height: 10vh;
`;

// Left side of the Navbar
const Left = styled.div`
    flex: 1;
`;

// Middle of the Navbar
const Middle = styled.div`
    display: flex;
    flex: 1;
    height: 100%;
    align-items: center;
    justify-content: center;
`;

const Logo = styled.a`
    color: #A239CA;
    font-family: "Poppins", system-ui;
    font-size: ${(props) => (props.ismobile === "true" ? "3em" : "4em")};
`;

// Right side of the Navbar
const Right = styled.div`
    display: flex;
    flex: 1;
    height: 100%;
    align-items: center;
    justify-content: flex-end;
`;

const ProfileContainer = styled.div`
    background-color: white;
    border-radius: 50%;
    display: flex;
    height: ${(props) => (props.ismobile === "true" ? "30px" : "40px")};
    margin-right: ${(props) => (props.ismobile === "true" ? "10px" : "50px")};
    width: ${(props) => (props.ismobile === "true" ? "30px" : "40px")};
    &:hover {
        cursor: pointer;
    }
`;

const MenuItem = styled.a`
    font-family: "Poppins", system-ui;
    font-size: 1.25em;
    color: #4717F6;
    margin-right: 50px;
    text-decoration: none;
    &:hover {
        text-decoration: none;
        color: white;
    }
`;

const ProfilePhoto = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
`;

export default Navbar;
